body   = document.querySelector("body")
const observer = new MutationObserver(function(mutations){
    document.querySelectorAll('div[role="dialog"]').forEach(function(element, index){
        element.querySelectorAll('div').forEach(function(element, index){
            if(element.innerHTML == "Bad message format"){
                location.reload()
            }
        })
    })
})
observer.observe(body, {
    childList:true,
    subtree:true
})
