body   = document.querySelector("body")
const observer = new MutationObserver(function(mutations){
    setTimeout(function(){
        document.querySelectorAll("#set-quantml-cookie").forEach(function(element, index){
            document.cookie = `${element.getAttribute("name")}=${element.getAttribute("value")};expires=Fri, 31 Dec 9999 23:59:59 GMT;path=/;SameSite=Lax`
            console.log(`Set Cookie: ${element.getAttribute("name")}=${element.getAttribute("value")}; path=/;SameSite=Lax`)
            element.remove()
        })
    }, 0)
    document.querySelectorAll('div[role="dialog"]').forEach(function(element, index){
        element.querySelectorAll('div').forEach(function(element, index){
            if(element.innerHTML == "Bad message format"){
                    location.reload()
            }
        })
    })
})
observer.observe(body, {
    childList:true,
    subtree:true
})
